export default function bubbleSort(arr) {
    var animationSeq = getAnimationSeq(arr);

}

function getAnimationSeq(arr) {
    for (i = 0; i < n - 1; i++) {
        for (j = 0; j < n - i - 1; j++) {
            if (arr[j] > arr[j+1]) {
                swap(arr,j,j+1);
            }
        }
    }
}
