import '../Styles/Sort.css';
import React, { useState, useEffect } from 'react';

import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Dialog from '@mui/material/Dialog';
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';

import ToggleButton from '@mui/material/ToggleButton';
import ToggleButtonGroup from '@mui/material/ToggleButtonGroup';

function initializeArray(arr, num) {
    for (var i = 0; i < num; i++) {
        arr[i] = Math.floor(Math.random() * (Math.round(window.innerHeight / 100) * 75)) + 15; // random between 1 & Math.round(window.innerHeight / 100)
    }
}

function Sort() {

    var array = [];

    return (
        <div className="Sort">

            <Form />

            <div className="Header">
                <div>Sorting Algorithms</div> {/* change after form submitted */}
                <div className="Form">

                </div>
            </div>
            <div className="Body">
                {initializeArray(array, 100)}
                {array.map((x, i) => (
                    <div className="bar" style={{height: `${x}px`}} key={i}/>
                ))}
            </div>
        </div>
    );
}

export default Sort;
