import '../Styles/Path.css';
import React, { useState, useEffect } from 'react';

import Button from '@mui/material/Button';
import { Link } from 'react-router-dom';

import dijkstraAlgorithm from '../Algorithms/PathFinding/dijkstra.js';

function initializeArray(num) {
    var arr = [];

    for (var i = 0; i < num; i++) {
        arr.push(Math.floor(Math.random() * (Math.round(window.innerHeight / 100) * 78)) + 10); // random between 1 & Math.round(window.innerHeight / 100)
    }

    return arr;
}

function Path() {

    const [array, setArray] = useState([]);

    return (
        <div className="Sort">

            <div className="Header">

                <Button className="Back" variant="contained" size="small" component={Link} to="../">Back</Button>

                <div className="Title">

                    <p>Path Finding Algorithms</p>
                </div>

                <div className="Form">

                    <Button variant="contained" size="small" onClick={dijkstraAlgorithm()}>Dijkstras</Button>
                </div>
            </div>
            <div className="Body">
                {array.map((x, i) => (
                    <div className="bar" style={{height: `${x}px`, width: `${400 / array.length}px`}} key={i}/>
                ))}
            </div>
        </div>
    );
}

export default Path;
