export default function bubbleSort(arr) {
    var animationSeq = getAnimationSeq(arr);
    for (var i = 0; i < animationSeq.length(); i++) {
        // color change
        if (animationSeq[0][0] == 0) {
            setTimeout(() => {
                arr[animationSeq[i][1]].style.backgroundColor = 'red';
                arr[animationSeq[i][2]].style.backgroundColor = 'red';
            }, 500);
            arr[animationSeq[i][1]].style.backgroundColor = '#f5f0e5';
            arr[animationSeq[i][2]].style.backgroundColor = '#f5f0e5';
        } else {
        // height change

        }
    }
}

function getAnimationSeq(arr) {
    var seq = [[]];

    var n = arr.length;

    for (var i = 0; i < n - 1; i++) {
        for (var j = 0; j < n - i - 1; j++) {
            seq.push([0, i, j]);
            if (arr[j] > arr[j+1]) {
                seq.push([1, i, j]);
            }
        }
    }
}
