import '../Styles/Sort.css';
import React, { useState, useEffect } from 'react';

function initializeArray(arr, num) {
    for (var i = 0; i < num; i++) {
        arr[i] = Math.floor(Math.random() * (Math.round(window.innerHeight / 100) * 75)) + 30; // random between 1 & Math.round(window.innerHeight / 100)
    }
}

function Sort() {

    var array = [];

    useEffect(() => { // change to after form is submitted
        array = initializeArray(array, 100);
    });

    console.log(array);

    return (
        <div className="Sort">
            <div className="Header">
                Sorting Algorithms {/* change after form submitted */}
            </div>
            <div className="Body">
                {initializeArray(array, 100)}
                {array.map((x, i) => (
                    <div className="bar" style={{height: `${x}px`}} key={i}/>
                ))}
            </div>
        </div>
    );
}

export default Sort;
