export class Node {
    constructor(dis, x, y, parent) {
        this.dis = dis;
        this.x = x;
        this.y = y;
        this.parent = parent;
    }

    get coords() {
        return [this.x, this.y];
    }
}


export default function dijkstraAlgorithm() {
