    for (var i = 0; i < rows.length; i++) {
        var box = rows[i].getElementsByClassName("box");
        var arr2 = [];
        for (var j = 0; j < box.length; j++) {
            if (box[j].style.backgroundColor == 'green') {
                var node = new Node(-1, i+1, j, null);
                arr2.push(node);
                var start = [i+1, j];
            } else if (box[j].style.backgroundColor == 'black') {
                var node = new Node(-1, i+1, j, null);
                arr2.push(node);
                var end = [i+1, j];
            } else if (box[j].style.backgroundColor == 'blue') {
                var node = new Node(-3, i+1, j, null);
                arr2.push(node);
            } else {
                var node = new Node(Number.MAX_SAFE_INTEGER, i+1, j, null);
                arr2.push(node);
            }
        }
        arr.push(arr2);
    }
    dijkstras(arr, start, end);
}

